import React, { useState } from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";
const Hotels = () => {
    const [location, setLocation] = useState("")
    const [checkInDate, setCheckInDate] = useState("")
    const [checkOutDate, setCheckOutDate] = useState("")
    const [numOfRooms, setNumOfRooms] = useState(0)
    const [hotelIDInfo, setHotelIDInfo] = useState(null)
    const [hotelData, setHotelData] = useState(null)
    const [numAdults, setNumAdults] = useState(0);

    const newHotelLocation = (e) => {
        setLocation(e.target.value)
    }

    const newCheckInDate = (e) => {
        setCheckInDate(e.target.value)
    }

    const newCheckOutDate = (e) => {
        setCheckOutDate(e.target.value)
    }


    const newNumOfRooms = (e) => {
        setNumOfRooms(e.target.value)
    }

    const newNumAdults = (e) => {
        setNumAdults(e.target.value);
    }

    const fetchHotelID = async () => {
        try {
            const apiAccessToken = await GenerateNewToken();

            let cityCode = location.trim().toUpperCase();

            // If input is not a valid 3-letter IATA code, try to resolve it
            if (cityCode.length !== 3 || /[^A-Z]/.test(cityCode)) {
                const cityResponse = await axios.get('https://test.api.amadeus.com/v1/reference-data/locations', {
                    headers: {
                        'Authorization': `Bearer ${apiAccessToken}`
                    },
                    params: {
                        keyword: location,
                        subType: 'CITY'
                    }
                });

                const cityData = cityResponse.data.data;

                if (!cityData || cityData.length === 0) {
                    console.log("No matching city found.");
                    setHotelIDInfo(null);
                    return;
                }

                cityCode = cityData[0].iataCode;
                console.log("Resolved city code:", cityCode);
            }

            const response = await axios.get('https://test.api.amadeus.com/v1/reference-data/locations/hotels/by-city', {
                headers: {
                    'Authorization': `Bearer ${apiAccessToken}`
                },
                params: {
                    cityCode: cityCode
                }
            });

            const hotelIDData = response.data.data;
            if (hotelIDData && hotelIDData.length > 0) {
                const firstHotelID = hotelIDData[0].hotelId;
                setHotelIDInfo(firstHotelID);
                console.log("Hotel ID:", firstHotelID);
                return firstHotelID;
            } else {
                console.log("No hotels found for this city.");
                setHotelIDInfo(null);
            }

        } catch (error) {
            console.log('Error fetching hotel ID:', error.response?.data || error.message);
        }
    };


    const fetchData = async (hotelID) => {
        try {
            const apiAccessToken = await GenerateNewToken();
            const response = await axios.get(
                'https://test.api.amadeus.com/v3/shopping/hotel-offers',
                {
                    headers: {
                        'Authorization': `Bearer ${apiAccessToken}`
                    },
                    params: {
                        hotelIds: hotelID,
                        checkInDate: checkInDate,
                        checkOutDate: checkOutDate,
                        adults: numAdults,
                        roomQuantity: numOfRooms,
                        currency: 'GBP'
                    }
                }
            );

            setHotelData(response.data)
        } catch (error) {
            console.log('Error fetching hotel data:', error.response?.data || error.message);
        }
    }

    const addHotels = () => {
        alert("Selena Gomez")
    }


    const submitForm = async (e) => {
        e.preventDefault();

        try {
            const hotelIDInfo = await fetchHotelID();
            if (hotelIDInfo) {
                fetchData(hotelIDInfo)
            } else {
                console.log("No hotel ID returned.");
            }
        } catch (error) {
            console.log("Error in submitForm:", error);
        }
    };

    return (
        <div className="container">
            <h1>Search for Hotels</h1>
            <form onSubmit={submitForm}>
                <div className="hotel-location">
                    <label>Location:</label>
                    <input type="text"
                        placeholder="Enter your destination"
                        value={location}
                        onChange={newHotelLocation}
                        required></input>
                </div>

                <div className="check-in-date">
                    <label>Check In Date:</label>
                    <input type="date"
                        value={checkInDate}
                        onChange={newCheckInDate}
                        required></input>
                </div>

                <div className="check-out-date">
                    <label>Check Out Date:</label>
                    <input type="date"
                        value={checkOutDate}
                        onChange={newCheckOutDate}
                        required></input>

                </div>


                <div className="number-rooms">
                    <label>Number of Rooms:</label>
                    <input type="number"
                        min={1}
                        max={10}
                        value={numOfRooms}
                        onChange={newNumOfRooms}
                        required></input>

                </div>

                <div className="people">
                    <label>Adults (13+):</label>
                    <input
                        type="number"
                        min={1}
                        max={9}
                        value={numAdults}
                        onChange={newNumAdults}
                        required
                    />
                </div>


                <div className="button">
                    <button className="submit-button"
                        type="submit">Find Hotels
                    </button>
                </div>
            </form>
            {hotelData?.data?.length > 0 && (
                <div className="hotel-results">

                    <h2>Hotel Results</h2>
                    {hotelData.data.map((hotel, index) => (
                        <div key={index} className="hotel-card" style={{ border: "1px solid #ccc", padding: "1rem", marginBottom: "1rem" }}>
                            <h3>{hotel.hotel.name}</h3>
                            <p>
                                {hotel.hotel.address?.lines?.join(", ")}, {hotel.hotel.address?.cityName}, {hotel.hotel.address?.countryCode} {hotel.hotel.address?.postalCode}
                            </p>
                            {hotel.offers.map((offer, i) => (
                                <div key={i} style={{ marginTop: "1rem" }}>
                                    <p><strong>Room Type:</strong> {offer.room?.typeEstimated?.category}</p>
                                    <p><strong>Price (in local currency):</strong> {offer.price?.total} {offer.price?.currency}</p>
                                    <p><strong>Guests:</strong> {offer.guests?.adults} Adults</p>
                                    <p><strong>Check-in:</strong> {offer.checkInDate}</p>
                                    <p><strong>Check-out:</strong> {offer.checkOutDate}</p>
                                    <button onClick={addHotels()}>Add to Itinerary</button>
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            )}



        </div>

    )

}
export default Hotels
