import React, { useState } from 'react';
import './TravelPreferencesForm.css'; // This is where we'll style it

const TravelPreferences = () => {
  const [formData, setFormData] = useState({
    travel_reason: '',
    budget_per_day: '',
    accommodation_type: '',
    destination_type: '',
    max_flight_duration: '',
    preferred_weather: '',
    travel_pace: '',
    favorite_activities: [],
    travel_companions: '',
    flight_priority: '',
  });

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleActivityToggle = (activity) => {
    setFormData(prev => {
      const exists = prev.favorite_activities.includes(activity);
      const updated = exists
        ? prev.favorite_activities.filter(a => a !== activity)
        : [...prev.favorite_activities, activity];
      return { ...prev, favorite_activities: updated };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted Preferences:', formData);
    // Handle data submission to backend or storage
  };

  const preferences = {
    travel_reason: ["Leisure/Vacation", "Business", "Adventure/Exploration", "Visiting friends or family", "Cultural or educational experience"],
    budget_per_day: ["Budget-friendly", "Mid-range", "Luxury", "Flexible"],
    accommodation_type: ["Budget hotel or hostel", "Mid-range hotel", "Luxury resort or 5-star hotel", "Airbnb or vacation rental", "Boutique or themed stays"],
    destination_type: ["Beaches and islands", "Mountains and nature", "Urban cities and nightlife", "Historical or cultural sites", "Countryside and quiet retreats"],
    max_flight_duration: ["Under 3 hours", "3–6 hours", "6–10 hours", "Any distance if it’s worth it"],
    preferred_weather: ["Warm and sunny", "Cool and breezy", "Cold and snowy", "Doesn’t matter"],
    travel_pace: ["Relaxed and slow", "Balanced", "Fast-paced"],
    travel_companions: ["Solo", "As a couple", "With friends", "With family", "With a tour group"],
    flight_priority: ["Lowest price", "Shortest travel time", "Most comfortable", "Best departure/arrival times", "Trusted airline"],
    favorite_activities: ["Outdoor/adventure", "Cultural experiences", "Shopping and nightlife", "Spa and wellness", "Family-friendly attractions"],
  };

  return (
    <form className="travel-form" onSubmit={handleSubmit}>
      <h2>Travel Preferences</h2>

      {Object.entries(preferences).map(([field, options]) =>
        field !== 'favorite_activities' ? (
          <div key={field} className="form-group">
            <label htmlFor={field}>{field.replace(/_/g, ' ')}</label>
            <select
              id={field}
              value={formData[field]}
              onChange={(e) => handleChange(field, e.target.value)}
            >
              <option value="">Select an option</option>
              {options.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
        ) : (
          <div key={field} className="form-group">
            <label>{field.replace(/_/g, ' ')}</label>
            <div className="checkbox-grid">
              {options.map(activity => (
                <label key={activity} className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={formData.favorite_activities.includes(activity)}
                    onChange={() => handleActivityToggle(activity)}
                  />
                  {activity}
                </label>
              ))}
            </div>
          </div>
        )
      )}

      <button type="submit" className="submit-button">Save Preferences</button>
    </form>
  );
};

export default TravelPreferences;
