import React, { useState, useEffect, useRef } from 'react';
import mapboxgl from 'mapbox-gl';  // Import Mapbox GL
import './Styles/LoadMap.css';
import axios from 'axios';

function LoadMap() {
  const containerRef = useRef(null);  // Ref for the map container
  const [location, setLocation] = useState('');
  const [centre, setCentre] = useState({ lat: 51.5074456, lng: -0.1277653 });  // Default location (London)
  const [map, setMap] = useState(null);

  mapboxgl.accessToken = "pk.eyJ1IjoiaHVzc2FtazI4IiwiYSI6ImNtYThlcTlyNTE2dXkybHNnNGZwMzEzMGwifQ.kK-mWZCSxk5o87_oISSSYQ";  // Use your Mapbox access token

  // Initialize the Mapbox map
  const initializeMap = () => {
    const mapInstance = new mapboxgl.Map({
      container: containerRef.current,  // The DOM element for the map
      style: 'mapbox://styles/mapbox/streets-v11',  // Map style
      center: [centre.lng, centre.lat],  // Set the center to the default coordinates
      zoom: 12  // Default zoom level
    });

    // Add navigation controls (zoom and rotation)
    mapInstance.addControl(new mapboxgl.NavigationControl(), 'top-left');
    setMap(mapInstance);  // Save the map instance in state for later use
  };

  useEffect(() => {
    if (!map) {
      initializeMap();
    } else {
      map.setCenter([centre.lng, centre.lat]);
    }
  }, [centre, map]);  // Re-run this when the center changes or map is null

  // Handle location search
  const handleLocationSearch = (e) => {
    setLocation(e.target.value);
  };

  const submitLocation = async (e) => {
    e.preventDefault();

    // Use Mapbox geocoding API to search for the location
    const geocodeURL = `https://api.mapbox.com/geocoding/v5/mapbox.places/${location}.json?access_token=${mapboxgl.accessToken}`;
    try {
      const response = await axios.get(geocodeURL);
      const coords = response.data.features[0].geometry.coordinates;
      setCentre({ lat: coords[1], lng: coords[0] });
    } catch (error) {
      alert('Location not found.');
      console.error(error);
    }
  };

  // Fetch wheelchair accessible places from an API
  const fetchWheelchairAccessiblePlaces = async (lat, lon) => {
    try {
      const response = await axios.get(`https://wheelmap.org/api/nodes?lat=${lat}&lon=${lon}&distance=1000&wheelchair=yes`, {
        headers: {
          'Accept': 'application/json',
        }
      });

      const wheelchairAccessibleMarkers = response.data.nodes.map(place => ({
        lat: place.lat,
        lng: place.lon,
        name: place.name,
        wheelchair: place.wheelchair,
      }));
      console.log("WheelMap Markers: ", wheelchairAccessibleMarkers);
      return wheelchairAccessibleMarkers;
    } catch (error) {
      console.error("Error fetching wheelchair accessible places:", error);
      return [];
    }
  };

  // Add markers to the map
  const addMarkers = (places) => {
    places.forEach(place => {
      new mapboxgl.Marker()
        .setLngLat([place.lng, place.lat])  // Set marker position
        .setPopup(new mapboxgl.Popup().setHTML(`<h3>${place.name}</h3><p>Accessible: ${place.wheelchair}</p>`))
        .addTo(map);  // Add the marker to the map
    });
  };

  const submitForm = async (e) => {
    e.preventDefault();

    // Fetch wheelchair accessible places and add them as markers on the map
    const wheelchairMarkers = await fetchWheelchairAccessiblePlaces(centre.lat, centre.lng);
    addMarkers(wheelchairMarkers);
  };

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={handleLocationSearch}
            required
          />
          <button className="submit-button" type="submit">Search</button>
        </div>
      </form>

      {/* Map container */}
      <div
        ref={containerRef}  // Attach the ref to the map container
        style={{ height: '650px', width: '100%' }}  // Map container style
      ></div>
    </div>
  );
}

export default LoadMap;
