import axios from "axios";
import React, { useState } from "react";
import './Styles/Login.css'
const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("")

    const writeEmail = (e) => {
        setEmail(e.target.value)
    }
    const writePassword = (e) => {
        setPassword(e.target.value)
    }

    const loginAccount = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://127.0.0.1:8000/accountExists/", {email, password})
            if (response.data.user.id){
                localStorage.setItem("userID", response.data.user.id)
                alert(response.data.message || "Login Successful!")
            }
            else {
                alert("User ID not avaliable")
            }

        } catch (error) {
            console.error(error)
            alert("Email or password incorrect, please try again!")

        }
    }
    return (
        <div className="Container">
            <h1>Login</h1>
            <form onSubmit={loginAccount}>
            <div className="email-address">
                <input 
                type='email' 
                placeholder="Enter Email Address"
                value={email}
                onChange={writeEmail}
                required></input>    
            </div>
            <div className="password">
                <input 
                type='password' 
                placeholder="Enter Password"
                value={password}
                onChange={writePassword}
                pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" 
                title="Must contain at least one number and one uppercase and lowercase letter, and 
                at least 8 or more characters"
                required></input>    
            </div>

            <div className="button">
                <button className="Login-button"type="submit">Login </button>

                <button className="clear-button" type="button">Clear</button>
            </div>

            </form>
        </div>
    )

}
export default Login
