const Flights = () => {
    const [depAirport, setDepAirport] = useState("");
    const [arrAirport, setArrAirport] = useState("");
    const [depDate, setDepDate] = useState("");
    const [retDate, setRetDate] = useState("");
    const [flightData, setFlightData] = useState(null);
    const [numAdults, setNumAdults] = useState(0);
    const [numChildren, setNumChildren] = useState(0);
    const [numInfants, setNumInfants] = useState(0);

    const newDepAirport = (e) => { setDepAirport(e.target.value); };
    const newArrAirport = (e) => { setArrAirport(e.target.value); };
    const newDepDate = (e) => { setDepDate(e.target.value); };
    const newRetDate = (e) => { setRetDate(e.target.value); };
    const newNumAdults = (e) => { setNumAdults(e.target.value); };
    const newNumChildren = (e) => { setNumChildren(e.target.value); };
    const newNumInfants = (e) => { setNumInfants(e.target.value); };

    const addToItineraryFlights = async (flightOffer) => {
        try {
            const response = await axios.post("http://127.0.0.1:8000/addToItineraryFlights/", {
                airline: flightOffer.airline,
                flightNum: flightOffer.flightNum,
                departureAirport: flightOffer.departureAirport,
                departureDate: flightOffer.departureDate,
                departureTime: flightOffer.departureTime,
                arrivalAirport: flightOffer.arrivalAirport,
                arrivalDate: flightOffer.arrivalDate,
                arrivalTime: flightOffer.arrivalTime,
                duration: flightOffer.duration,
                price: flightOffer.price
            });
            alert(response.data.message || "Added to itinerary");
        } catch (error) {
            console.error(error);
            alert("We were unable to add flight to itinerary");
        }
    };

    const fetchData = async () => {
        try {
            const apiAccessToken = await GenerateNewToken();
            const response = await axios.get('https://test.api.amadeus.com/v2/shopping/flight-offers', {
                headers: { 'Authorization': `Bearer ${apiAccessToken}` },
                params: {
                    originLocationCode: depAirport,
                    destinationLocationCode: arrAirport,
                    departureDate: depDate,
                    returnDate: retDate,
                    adults: numAdults,
                    children: numChildren,
                    infants: numInfants,
                    nonStop: true,
                    currencyCode: 'GBP',
                    max: 20
                }
            });
            setFlightData(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const submitForm = (e) => {
        e.preventDefault();
        fetchData();
    };

    return (
        <div className="container">
            <h1>Search for Flights</h1>
            <form onSubmit={submitForm}>
                {/* Form Inputs */}
                <div className="departure-airport">
                    <label>From:</label>
                    <input type="text" placeholder="Enter 3 Letters" value={depAirport} onChange={newDepAirport} required />
                </div>
                <div className="arrival-airport">
                    <label>To:</label>
                    <input type="text" placeholder="Enter 3 Letters" value={arrAirport} onChange={newArrAirport} required />
                </div>
                <div className="departure-date">
                    <label>Departure Date:</label>
                    <input type="date" value={depDate} onChange={newDepDate} required />
                </div>
                <div className="return-date">
                    <label>Return Date:</label>
                    <input type="date" value={retDate} onChange={newRetDate} required />
                </div>
                <div className="people">
                    <label>Adults (13+):</label>
                    <input type="number" value={numAdults} onChange={newNumAdults} required />
                    <label>Children (2-12):</label>
                    <input type="number" value={numChildren} onChange={newNumChildren} required />
                    <label>Infants (0-2):</label>
                    <input type="number" value={numInfants} onChange={newNumInfants} required />
                </div>
                <div className="button">
                    <button className="submit-button" type="submit">Find Flights</button>
                </div>
            </form>

            {/* Displaying flight data */}
            {flightData?.data?.length > 0 && (
                <div>
                    {flightData.data.map((flightOffer, index) => (
                        <div key={index} className="flight-offer">
                            <h2>Option {index + 1}</h2>
                            <p>Total Price: {flightOffer.price}</p>
                            <div className="segment">
                                <p><strong>Airline:</strong> {flightOffer.airline}</p>
                                <p><strong>Flight Number:</strong> {flightOffer.flightNum}</p>
                                <p><strong>Departure Airport:</strong> {flightOffer.departureAirport}</p>
                                <p><strong>Departure Date:</strong> {flightOffer.departureDate}</p>
                                <p><strong>Departure Time:</strong> {flightOffer.departureTime}</p>
                                <p><strong>Arrival Airport:</strong> {flightOffer.arrivalAirport}</p>
                                <p><strong>Arrival Date:</strong> {flightOffer.arrivalDate}</p>
                                <p><strong>Arrival Time:</strong> {flightOffer.arrivalTime}</p>
                                <p><strong>Duration:</strong> {flightOffer.duration}</p>
                                <button onClick={() => addToItineraryFlights(flightOffer)}>Add to Itinerary</button>

                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Flights;
