import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './Styles/LoadMap.css'
import axios from 'axios';

// This is the load map function which sets the size of the google map
function LoadMap() {
  const containerStyle = {
    width: '100%',
    height: '650px'
  };

  // This sets the default centre of the map to London when page first loads
  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  // This is the getter and setter variables that are used on this page
  const [location, setLocation] = useState('');
  const [mapMarkers, setMapMarkers] = useState([]);
  const [selectedMapMarkers, setSelectedMapMarkers] = useState(null)
  const [PopupBox, setPopupBox] = useState(false)
  const [centre, setCentre] = useState(defaultCentre);
  const mapRef = useRef(null);

  // This takes in the location inputted by the user
  const newLocation = (e) => {
    setLocation(e.target.value);
  };

  // Fetch wheelchair accessible places using Overpass API (OSM)
  const fetchOSMWheelchairAccessiblePlaces = async (lat, lon) => {
    const query = `
      [out:json];
      (
        node["wheelchair"="yes"](around:1000,${lat},${lon});
        way["wheelchair"="yes"](around:1000,${lat},${lon});
        relation["wheelchair"="yes"](around:1000,${lat},${lon});
      );
      out body;
    `;

    try {
      const response = await axios.get(`https://overpass-api.de/api/interpreter`, {
        params: { data: query }
      });
      const wheelchairAccessibleMarkers = response.data.elements.map(place => ({
        lat: place.lat || place.center.lat,
        lon: place.lon || place.center.lon,
        name: place.tags.name || 'Unnamed place',
        wheelchair: true,
      }));
      return wheelchairAccessibleMarkers;
    } catch (error) {
      console.error("Error fetching OSM wheelchair accessible places:", error);
      return [];
    }
  };

  // Submit form, trigger map search and fetch wheelchair accessible places
  const submitForm = async (e) => {
    e.preventDefault();

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: location }, async (results, status) => {
      if (status === 'OK' && results.length > 0) {
        const coords = results[0].geometry.location;
        setCentre({ lat: coords.lat(), lng: coords.lng() });

        // Google Places API
        const service = new window.google.maps.places.PlacesService(mapRef.current);
        const types = ['tourist_attraction', 'restaurant', 'museum', 'amusement_park', 'shopping_mall', 'park'];

        const searchByType = (type) => {
          return new Promise((resolve) => {
            const request = {
              location: coords,
              radius: 10000,
              type: type,
              keyword: "wheelchair accessible",
            };

            service.nearbySearch(request, (results, status) => {
              if (status === window.google.maps.places.PlacesServiceStatus.OK) {
                const markers = results.map(place => ({
                  lat: place.geometry.location.lat(),
                  lng: place.geometry.location.lng(),
                  name: place.name,
                }));
                resolve(markers);
              } else {
                resolve([]);
              }
            });
          });
        };

        const resultsArrays = await Promise.all(types.map(searchByType));

        // Fetch wheelchair accessible places from OSM
        const osmWheelMarkers = await fetchOSMWheelchairAccessiblePlaces(coords.lat(), coords.lng());

        // Combine Google Places and OSM markers
        const combinedMarkers = [...resultsArrays.flat(), ...osmWheelMarkers];
        setMapMarkers(combinedMarkers);
      } else {
        alert('Location not found.');
      }
    });
  };

  // Add to itinerary functionality
  const addToItinerary = async (placeName) => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/addToItinerary/", { name: placeName });
      alert(response.data.message || "Added to itinerary");
    } catch (error) {
      console.error(error);
      alert("We were unable to add attraction");
    }
  };

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={newLocation}
            required
          />
          <button className="submit-button" type="submit">Search</button>
        </div>
      </form>

      <LoadScript
        googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
        libraries={['places']}
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={centre}
          zoom={10}
          onLoad={map => (mapRef.current = map)}
        >
          {mapMarkers.map((mapMarker, index) => (
            <Marker
              key={index}
              position={{ lat: mapMarker.lat, lng: mapMarker.lon }}
              onClick={() => {
                setSelectedMapMarkers(mapMarker);
                setPopupBox(true);
              }}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {PopupBox && selectedMapMarkers && (
        <div className="modal-backdrop" onClick={() => setPopupBox(false)}>
          <div className="modal-box" onClick={e => e.stopPropagation()}>
            <h2>{selectedMapMarkers.name}</h2>
            <p><strong>Note:</strong> This place may support wheelchair access (based on keyword match).</p>
            <button onClick={() => addToItinerary(selectedMapMarkers.name)}>Add to Itinerary</button>
            <button onClick={() => setPopupBox(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoadMap;
