import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './Styles/Navbar.css';

const Navbar = () => {
    const [dropdownMenu, setDropdownMenu] = useState(false);
    const [loginStatus, setLoginStatus] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const userID = localStorage.getItem("userID");
        setLoginStatus(!!userID);
    }, []);

    const logOut = () => {
        localStorage.removeItem("userID");
        setLoginStatus(false);
        navigate("/Login");
    };

    const dropdownOpen = () => {
        setDropdownMenu(!dropdownMenu);
    };

    return (
        <nav>
            <ul>
                <li><Link to='/Flights'>Flights</Link></li>
                <li><Link to='/Hotels'>Hotels</Link></li>
                <li><Link to='/Activities'>Activities</Link></li>
                <li><Link to='/Itinerary'>Itinerary</Link></li>
                <li><Link to='/travelPreferences'>Travel Preferences</Link></li>
                <li><Link to='/userRecommendations'>User Recommendations</Link></li>
                <li className="dropdown">
                    <button onClick={dropdownOpen}>My Account</button>
                    {dropdownMenu && (
                        <ul className="dropdown-menu">
                            {!loginStatus ? (
                                <>
                                    <li><Link to='/Login' onClick={() => setDropdownMenu(false)}>Login</Link></li>
                                    <li><Link to='/' onClick={() => setDropdownMenu(false)}>Register Account</Link></li>
                                </>
                            ) : (
                                <li><button onClick={logOut}>Logout</button></li>
                            )}
                        </ul>
                    )}
                </li>
            </ul>
        </nav>
    );
};

export default Navbar;
