import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

function LoadMap() {
  const containerStyle = {
    width: '100%',
    height: '650px'
  };

  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  const [location, setLocation] = useState('');
  const [mapMarkers, setMapMarkers] = useState([]);
  const [centre, setCentre] = useState(defaultCentre);
  const [selectedMarker, setSelectedMarker] = useState(null)
  const mapRef = useRef(null);

  const newLocation = (e) => {
    setLocation(e.target.value);
  };

  const submitForm = async (e) => {
    e.preventDefault();

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: location }, (results, status) => {
      if (status === 'OK' && results.length > 0) {
        const coords = results[0].geometry.location;
        setCentre({ lat: coords.lat(), lng: coords.lng() });

        // Now search for tourist attractions
        const service = new window.google.maps.places.PlacesService(mapRef.current);

        const request = {
          location: coords,
          radius: 10000,
          type: 'tourist_attraction',
        };

        service.nearbySearch(request, (results, status) => {
          if (status === window.google.maps.places.PlacesServiceStatus.OK) {
            const newMarkers = results.map(place => ({
              lat: place.geometry.location.lat(),
              lng: place.geometry.location.lng(),
              name: place.name
            }));
            setMapMarkers(newMarkers);
          }
        });
      } else {
        alert('Location not found.');
      }
    });
  };

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={newLocation}
            required
          />
          <button className="submit-button" type="submit">Search</button>
        </div>
      </form>

      <LoadScript
        googleMapsApiKey="AIzaSyBcMsINbIrAZ_gChuZvQ_6SWD2hdFowrWc"
        libraries={['places']} // <-- important to enable PlacesService
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={centre}
          zoom={13}
          onLoad={map => (mapRef.current = map)}
        >
          {mapMarkers.map((marker, index) => (
            <React.Fragment key={index}>
              <Marker
                position={{ lat: marker.lat, lng: marker.lng }}
                onClick={() => setSelectedMarker(marker)}
              />
              {selectedMarker && selectedMarker.lat === marker.lat && selectedMarker.lng === marker.lng && (
                <InfoWindow
                  position={{ lat: selectedMarker.lat, lng: selectedMarker.lng }}
                  onCloseClick={() => setSelectedMarker(null)}
                >
                  <div>
                    <h4>{selectedMarker.name}</h4>
                  </div>
                </InfoWindow>
              )}
            </React.Fragment>
          ))}
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default LoadMap;
