import React, { useState } from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";

const Hotels = () => {
    const [locationInput, setLocationInput] = useState("");
    const [suggestions, setSuggestions] = useState([]);
    const [selectedCityCode, setSelectedCityCode] = useState("");
    const [checkInDate, setCheckInDate] = useState("");
    const [checkOutDate, setCheckOutDate] = useState("");
    const [numOfPeople, setNumOfPeople] = useState("");
    const [numOfRooms, setNumOfRooms] = useState("");
    const [hotels, setHotels] = useState([]);

    const handleLocationChange = async (e) => {
        const query = e.target.value;
        setLocationInput(query);
        setSelectedCityCode(""); // reset in case user edits input

        if (query.length < 3) {
            setSuggestions([]);
            return;
        }

        try {
            const token = await GenerateNewToken();
            const res = await axios.get("https://test.api.amadeus.com/v1/reference-data/locations", {
                headers: { Authorization: `Bearer ${token}` },
                params: {
                    keyword: query,
                    subType: "CITY"
                }
            });

            const mappedSuggestions = res.data.data.map((item) => ({
                name: item.address.cityName,
                code: item.iataCode
            }));
            setSuggestions(mappedSuggestions);
        } catch (err) {
            console.error("Error fetching suggestions:", err);
        }
    };

    const handleSelectSuggestion = (suggestion) => {
        setLocationInput(suggestion.name);
        setSelectedCityCode(suggestion.code);
        setSuggestions([]);
    };

    const fetchData = async () => {
        if (
            !selectedCityCode ||
            !checkInDate ||
            !checkOutDate ||
            !numOfPeople ||
            !numOfRooms
        ) {
            alert("Please fill in all required fields and select a city from the suggestions.");
            return;
        }

        try {
            const token = await GenerateNewToken();

            const response = await axios.get("https://test.api.amadeus.com/v3/shopping/hotel-offers", {
                headers: { Authorization: `Bearer ${token}` },
                params: {
                    cityCode: selectedCityCode,
                    checkInDate,
                    checkOutDate,
                    roomQuantity: parseInt(numOfRooms),
                    adults: parseInt(numOfPeople),
                    currency: "GBP"
                }
            });

            setHotels(response.data.data);
        } catch (error) {
            console.error("Error fetching hotels:", error);
        }
    };

    const submitForm = async (e) => {
        e.preventDefault();
        await fetchData();
    };

    return (
        <div className="container">
            <h1>Search for Hotels</h1>
            <form onSubmit={submitForm}>
                <div className="hotel-location" style={{ position: "relative" }}>
                    <label>Location:</label>
                    <input
                        type="text"
                        placeholder="Enter your destination"
                        value={locationInput}
                        onChange={handleLocationChange}
                        required
                        autoComplete="off"
                    />
                    {suggestions.length > 0 && (
                        <ul className="suggestions" style={{
                            position: "absolute",
                            background: "white",
                            border: "1px solid #ccc",
                            listStyle: "none",
                            padding: 0,
                            margin: 0,
                            width: "100%",
                            zIndex: 10
                        }}>
                            {suggestions.map((suggestion, i) => (
                                <li
                                    key={i}
                                    onClick={() => handleSelectSuggestion(suggestion)}
                                    style={{ padding: "8px", cursor: "pointer" }}
                                >
                                    {suggestion.name} ({suggestion.code})
                                </li>
                            ))}
                        </ul>
                    )}
                </div>

                <div className="check-in-date">
                    <label>Check In Date:</label>
                    <input
                        type="date"
                        value={checkInDate}
                        onChange={(e) => setCheckInDate(e.target.value)}
                        required
                    />
                </div>

                <div className="check-out-date">
                    <label>Check Out Date:</label>
                    <input
                        type="date"
                        value={checkOutDate}
                        onChange={(e) => setCheckOutDate(e.target.value)}
                        required
                    />
                </div>

                <div className="number-people">
                    <label>Number of People:</label>
                    <input
                        type="number"
                        min={1}
                        max={20}
                        value={numOfPeople}
                        onChange={(e) => setNumOfPeople(e.target.value)}
                        required
                    />
                </div>

                <div className="number-rooms">
                    <label>Number of Rooms:</label>
                    <input
                        type="number"
                        min={1}
                        max={20}
                        value={numOfRooms}
                        onChange={(e) => setNumOfRooms(e.target.value)}
                        required
                    />
                </div>

                <div className="button">
                    <button type="submit">Find Hotels</button>
                </div>
            </form>

            {hotels.length > 0 && (
                <div className="hotel-results">
                    <h2>Available Hotels:</h2>
                    {hotels.map((hotel, index) => (
                        <div key={index} className="hotel-card">
                            <h3>{hotel.hotel.name}</h3>
                            <p>{hotel.hotel.address.lines.join(", ")}, {hotel.hotel.address.cityName}</p>
                            <p><strong>Price:</strong> {hotel.offers[0]?.price?.total} {hotel.offers[0]?.price?.currency}</p>
                            <p><strong>Room Type:</strong> {hotel.offers[0]?.room?.typeEstimated?.category}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Hotels;
