import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './Styles/LoadMap.css';
import axios from 'axios';

function LoadMap() {
  const containerStyle = {
    width: '100%',
    height: '650px',
  };

  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653,
  };

  const [location, setLocation] = useState('');
  const [mapMarkers, setMapMarkers] = useState([]);
  const [selectedMapMarkers, setSelectedMapMarkers] = useState(null);
  const [PopupBox, setPopupBox] = useState(false);
  const [centre, setCentre] = useState(defaultCentre);
  const mapRef = useRef(null);

  const newLocation = (e) => {
    setLocation(e.target.value);
  };

  const fetchGooglePlacesData = async (lat, lng) => {
    const service = new window.google.maps.places.PlacesService(mapRef.current);
    const request = {
      location: new window.google.maps.LatLng(lat, lng),
      radius: 5000,
      type: ['tourist_attraction', 'restaurant', 'museum', 'shopping_mall', 'park'],
    };

    return new Promise((resolve) => {
      service.nearbySearch(request, (results, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          const places = results.map((place) => ({
            lat: place.geometry.location.lat(),
            lng: place.geometry.location.lng(),
            name: place.name,
            address: place.vicinity || 'Address not available',
            wheelchair: place.accessibility ? 'Wheelchair Accessible' : 'Not Accessible', // Check if wheelchair info is available
            photo: place.photos ? place.photos[0].getUrl({ maxWidth: 400 }) : null,
          }));
          resolve(places);
        } else {
          resolve([]);
        }
      });
    });
  };

  const fetchWheelmapData = async (lat, lng) => {
    const radius = 5000;
    const wheelmapUrl = `https://wheelmap.org/api/places.json?lat=${lat}&lon=${lng}&radius=${radius}`;

    try {
      const response = await axios.get(wheelmapUrl);
      const wheelmapPlaces = response.data.map((place) => ({
        lat: place.lat,
        lng: place.lon,
        name: place.title,
        address: place.address || 'Address not available',
        wheelchair: place.accessible ? 'Wheelchair Accessible' : 'Not Accessible',
        photo: place.photo_url ? place.photo_url : null,
      }));

      return wheelmapPlaces;
    } catch (error) {
      console.error('Error fetching Wheelmap data:', error);
      return [];
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: location }, async (results, status) => {
      if (status === 'OK' && results.length > 0) {
        const coords = results[0].geometry.location;
        const lat = coords.lat();
        const lng = coords.lng();
        setCentre({ lat, lng });

        const googleMarkers = await fetchGooglePlacesData(lat, lng);
        const wheelmapMarkers = await fetchWheelmapData(lat, lng);

        const allCombinedMarkers = [...googleMarkers, ...wheelmapMarkers];
        setMapMarkers(allCombinedMarkers);
      } else {
        alert('Location not found.');
      }
    });
  };

  const addToItinerary = async (placeName) => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/addToItinerary/', { name: placeName });
      alert(response.data.message || 'Added to itinerary');
    } catch (error) {
      console.error(error);
      alert('We were unable to add attraction');
    }
  };

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={newLocation}
            required
          />
          <button className="submit-button" type="submit">
            Search
          </button>
        </div>
      </form>

      <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY} libraries={['places']}>
        <GoogleMap mapContainerStyle={containerStyle} center={centre} zoom={13} onLoad={(map) => (mapRef.current = map)}>
          {mapMarkers.map((marker, index) => (
            <Marker
              key={index}
              position={{ lat: marker.lat, lng: marker.lng }}
              onClick={() => {
                setSelectedMapMarkers(marker);
                setPopupBox(true);
              }}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {PopupBox && selectedMapMarkers && selectedMapMarkers.photo && (
        <div className="modal-backdrop" onClick={() => setPopupBox(false)}>
          <div className="modal-box" onClick={(e) => e.stopPropagation()}>
            <h1>{selectedMapMarkers.name}</h1>
            <h2>{selectedMapMarkers.address}</h2>
            <h3>wheelchair-accessible: {selectedMapMarkers.wheelchair}</h3>
            {selectedMapMarkers.photo && <img className="marker-photo" src={selectedMapMarkers.photo} />}
            <button onClick={() => addToItinerary(selectedMapMarkers.name)}>Add to Itinerary</button>
            <button onClick={() => setPopupBox(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoadMap;
