import React, { useEffect, useRef, useState } from 'react';
import {
  GoogleMap,
  LoadScript,
  Marker,
  Autocomplete
} from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '500px'
};

const defaultCenter = {
  lat: 40.7128,
  lng: -74.0060
};

function PlacesMap() {
  const mapRef = useRef(null);
  const [center, setCenter] = useState(defaultCenter);
  const [places, setPlaces] = useState([]);
  const [autocomplete, setAutocomplete] = useState(null);

  const onLoad = map => {
    mapRef.current = map;
    loadNearbyPlaces(center, map);
  };

  const loadNearbyPlaces = (location, map) => {
    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location,
      radius: 5000,
      type: ['tourist_attraction']
    };

    service.nearbySearch(request, (results, status) => {
      if (status === window.google.maps.places.PlacesServiceStatus.OK) {
        setPlaces(results);
      }
    });
  };

  // 🔍 Called when search button is clicked
  const handleSearchClick = () => {
    if (autocomplete) {
      const place = autocomplete.getPlace();
      if (place && place.geometry) {
        const location = place.geometry.location;
        const newCenter = {
          lat: location.lat(),
          lng: location.lng()
        };
        setCenter(newCenter);
        mapRef.current.panTo(newCenter);
        loadNearbyPlaces(newCenter, mapRef.current);
      } else {
        alert('Please select a place from the suggestions.');
      }
    }
  };

  return (
    <LoadScript
      googleMapsApiKey='AIzaSyBcMsINbIrAZ_gChuZvQ_6SWD2hdFowrWc'
      libraries={['places']}
    >
      <div style={{ marginBottom: '1rem', display: 'flex', gap: '10px' }}>
        <Autocomplete
          onLoad={auto => setAutocomplete(auto)}
        >
          <input
            type="text"
            placeholder="Search a city or place..."
            style={{
              width: '300px',
              height: '40px',
              padding: '0 12px',
              fontSize: '16px'
            }}
          />
        </Autocomplete>
        <button
          onClick={handleSearchClick}
          style={{
            height: '40px',
            padding: '0 16px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          Search
        </button>
      </div>

      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={13}
        onLoad={onLoad}
      >
        {places.map((place, idx) => (
          <Marker
            key={idx}
            position={place.geometry.location}
            title={place.name}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
}

export default PlacesMap;
