import React, { useState, useRef } from 'react';
// imports modules from Google Map's API that we need such as the map, its markers and autocomplete any location searches
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
//const GoogleMapsAPIKEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;



function LoadMap() {
  // This function sets the size of the map
  const containerStyle = {
    width: '100%',
    height: '800px'
  };

  // The default centre of the map is London 
  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  const [city, setCity] = useState("")
  const [mapMarkers, setMapMarkers] = useState([])
  const [centre, setCentre] = useState(defaultCentre)
  const mapRef = useRef(null)

  const newCity = (e) => {
    setCity(e.target.value)
  }

  const submitForm = (e) => {
    e.preventDefault()
    fetchData()
  }

  const fetchData = async () => {
    const fetchLocationData = await fetch(`https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${coordinates.lat},${coordinates.lng}&radius=5000&type=tourist_attraction&key=AIzaSyBcMsINbIrAZ_gChuZvQ_6SWD2hdFowrWc`);
    const locationDataJSON = await fetchLocationData.json()
    if (locationDataJSON.results.length > 0) {
      const coordinates = locationDataJSON.results[0].geometry.location
      setCentre(coordinates)
    }

    const fetchTouristAttractions = await fetch(`https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${coordinates.lat},${coordinates.lng}&radius=5000&type=tourist_attraction&key=AIzaSyBcMsINbIrAZ_gChuZvQ_6SWD2hdFowrWc`);
    const touristAttractionsJSON = await fetchTouristAttractions.json()
    if (touristAttractionsJSON.results) {
      const newMarkers = touristAttractionsJSON.results.map(place => ({
        lat: place.geometry.location.lat,
        lng: place.geometry.location.lng,
        name: place.name
      }))
      setMapMarkers(newMarkers)
    }
    loadUpdatedMap()
  }

  const loadUpdatedMap = () => {
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={centre}
      zoom={13}
      onLoad={map => (mapRef.current = map)}
    >
      {mapMarkers.map((mapMarkers, index) => (
        <Marker key={index} position={{ lat: mapMarkers.lat, lng: mapMarkers.lng }} title={mapMarkers.name} />
      ))}
    </GoogleMap>
  }

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>City:</label>
          <input type="text" placeholder="Enter your Location" value={city} onChange={newCity} required></input>
          <button className="submit-button" type="submit">Search</button>

        </div>
      </form>

      <LoadScript googleMapsApiKey='AIzaSyBcMsINbIrAZ_gChuZvQ_6SWD2hdFowrWc'>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={defaultCentre}
          zoom={12}
        >
          <Marker position={defaultCentre} />
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default LoadMap;
