import React, { useState, useRef } from 'react';
// imports modules from Google Map's API that we need such as the map, its markers and autocomplete any location searches
import { GoogleMap, LoadScript, Marker} from '@react-google-maps/api';
//const GoogleMapsAPIKEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;



function LoadMap() {
  // This function sets the size of the map
  const containerStyle = {
    width: '100%',
    height: '800px'
  };

  // The default centre of the map is London 
  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  const [location, setLocation] = useState("")
  const [mapMarkers, setMapMarkers] = useState([])
  const [centre, setCentre] = useState(defaultCentre)
  const mapRef = useRef(null)

    const  newLocation = (e) => {
        setLocation(e.target.value)
    }

    const submitForm = (e) => {
        e.preventDefault()
        //fetchData()
    }

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input type="text" placeholder="Enter your Location" value={location} onChange={newLocation} required></input>
          <button className="submit-button" type="submit">Search</button>

        </div>
      </form>

      <LoadScript googleMapsApiKey='AIzaSyBcMsINbIrAZ_gChuZvQ_6SWD2hdFowrWc'>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={defaultCentre}
          zoom={12}
        >
          <Marker position={defaultCentre} />
        </GoogleMap>
      </LoadScript>
    </div>
  );
}

export default LoadMap;
