import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './Styles/LoadMap.css'
import axios from 'axios';

// This is the load map function which sets the size of the google map
function LoadMap() {
  const containerStyle = {
    width: '100%',
    height: '650px'
  };

  // This sets the default centre of the map to London when page first loads
  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  // This is the getter and setter variabes that are used on this page
  const [location, setLocation] = useState('');
  const [mapMarkers, setMapMarkers] = useState([]);
  const [selectedMapMarkers, setSelectedMapMarkers] = useState(null)
  const [PopupBox, setPopupBox] = useState(false)
  const [centre, setCentre] = useState(defaultCentre);
  const mapRef = useRef(null);

  // This takes in the location inputted by the user
  const newLocation = (e) => {
    setLocation(e.target.value);
  };

  const fetchWheelchairAccessiblePlaces = async (lat, lon) => {
    const query = `
    (
      node["tourism"="attraction"](around:5000,${lat},${lon});
      way["tourism"="attraction"](around:5000,${lat},${lon});
      node["amenity"="restaurant"](around:5000,${lat},${lon});
      way["amenity"="restaurant"](around:5000,${lat},${lon});
      node["tourism"="museum"](around:5000,${lat},${lon});
      way["tourism"="museum"](around:5000,${lat},${lon});
      node["leisure"="amusement_park"](around:5000,${lat},${lon});
      way["leisure"="amusement_park"](around:5000,${lat},${lon});
      node["shop"](around:5000,${lat},${lon});
      way["shop"](around:5000,${lat},${lon});
      node["leisure"="park"](around:5000,${lat},${lon});
      way["leisure"="park"](around:5000,${lat},${lon});
    );
    out center tags;
  `;


    try {
      const response = await fetch("https://overpass-api.de/api/interpreter", {
        method: "POST",
        body: query,
      });

      const data = await response.json()
      const markers = data.elements.map((element) => {
        const coordinates = element.type === "node"
          ? { lat: element.lat, lng: element.lon } : { lat: element.center.lat, lng: element.center.lon };

        const isWheelchairAccessible = element.tags?.wheelchair || "unknown"

        let mapMarkerColour = '#808080'
        if (isWheelchairAccessible == "yes") {
          mapMarkerColour = 'green'
        }
          else if (isWheelchairAccessible == "limited") {
              mapMarkerColour = 'yellow'
          }
            else if (isWheelchairAccessible == "no") {
              mapMarkerColour = 'red';
            }

            return {
              lat: coordinates.lat,
              lng: coordinates.lng,
              name: element.tags?.name || "Place Name...",
              wheelchair: isWheelchairAccessible,
              markerColor: mapMarkerColour
            }
          });
          return markers
    } catch (error) {
      console.error("Error fetching data", error)
      return []

    }

  }

  // 
  const submitForm = async (e) => {
    e.preventDefault();

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: location }, async (results, status) => {
      if (status === 'OK' && results.length > 0) {
        const coords = results[0].geometry.location;
        const lat = coords.lat()
        const lng = coords.lng()
        setCentre({lat,lng});

        const markers = await fetchWheelchairAccessiblePlaces(lat, lng)
        setMapMarkers(markers);
      } else {
        alert("location not found")
      }
    });
  }



  const addToItinerary = async (placeName) => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/addToItinerary/", { name: placeName });
      alert(response.data.message || "Added to itinerary");
    } catch (error) {
      console.error(error);
      alert("We were unable to add attraction");
    }
  };


  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={newLocation}
            required
          />
          <button className="submit-button" type="submit">Search</button>
        </div>
      </form>

      <LoadScript
        googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
        libraries={['places']}
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={centre}
          zoom={10}
          onLoad={map => (mapRef.current = map)}
        >
          {mapMarkers.map((marker, index) => (
            <Marker
              key={index}
              position={{ lat: marker.lat, lng: marker.lng }}
              icon={{
                url: `http://maps.google.com/mapfiles/ms/icons/${marker.markerColor}-dot.png`,
                scaledSize: new window.google.maps.Size(32, 32),

              }}
              onClick={() => {
                setSelectedMapMarkers(marker)
                setPopupBox(true)
              }}
            />
          ))}
        </GoogleMap>
      </LoadScript>
      {PopupBox && selectedMapMarkers && (
        <div className="modal-backdrop" onClick={() => setPopupBox(false)}>
          <div className="modal-box" onClick={e => e.stopPropagation()}>
            <h2>{selectedMapMarkers.name}</h2>
            <p>
              <strong>Is this place wheelchair accessible: </strong>
              {selectedMapMarkers.wheelchair === "yes" && "Yes, wheelchair accessible"}
              {selectedMapMarkers.wheelchair === "limited" && "somewhat wheelchair accessible"}
              {selectedMapMarkers.wheelchair === "no" && "NOT wheelchair accessible"}
              {selectedMapMarkers.wheelchair === "unknown" && "wheelchair data not given"}
            </p>
            <button onClick={() => addToItinerary(selectedMapMarkers.name)}>Add Place</button>

            <button onClick={() => setPopupBox(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoadMap;
