import React, { useState } from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";
const Hotels = () => {
    const [location, setLocation] = useState("")
    const [checkInDate, setCheckInDate] = useState("")
    const [checkOutDate, setCheckOutDate] = useState("")
    const [numOfPeople, setNumOfPeople] = useState("")
    const [numOfRooms, setNumOfRooms] = useState("")
    const [hotelIDInfo, setHotelIDInfo] = useState(null)

    const newHotelLocation = (e) => {
        setLocation(e.target.value)
    }

    const newCheckInDate = (e) => {
        setCheckInDate(e.target.value)
    }

    const newCheckOutDate = (e) => {
        setCheckOutDate(e.target.value)
    }

    const newNumOfPeople = (e) => {
        setNumOfPeople(e.target.value)
    }

    const newNumOfRooms = (e) => {
        setNumOfRooms(e.target.value)
    }

    const fetchHotelID = async () => {
        try {
            const apiAccessToken = await GenerateNewToken();
            const response = await axios.get('https://test.api.amadeus.com/v1/reference-data/locations/hotels/by-city', {
                headers: {
                    'Authorization': `Bearer ${apiAccessToken}`
                },
                params: {
                    cityCode: location  // This should be a valid IATA city code (e.g. "PAR" for Paris)
                }
            });

            const hotelData = response.data.data;

        if (hotelData && hotelData.length > 0) {
            const firstHotel = hotelData[0];
            const hotelIDInfo = firstHotel.hotelId;

            setHotelIDInfo(hotelIDInfo);
            console.log("Hotel ID:", hotelIDInfo);
            return hotelIDInfo
        } else {
            console.log("No hotels found for the given location.");
            setHotelIDInfo(null);
        }
        } catch (error) {
            console.log('Error fetching hotel ID:', error.response?.data || error.message);
        }
    };


    const submitForm = (e) => {
        e.preventDefault()
        const hotelIDInfo = fetchHotelID()
        console.log(hotelIDInfo)
        //fetchData()
    }
    return (
        <div className="container">
            <h1>Search for Hotels</h1>
            <form onSubmit={submitForm}>
                <div className="hotel-location">
                    <label>Location:</label>
                    <input type="text"
                        placeholder="Enter your destination"
                        value={location}
                        onChange={newHotelLocation}
                        required></input>
                </div>

                <div className="check-in-date">
                    <label>Check In Date:</label>
                    <input type="date"
                        value={checkInDate}
                        onChange={newCheckInDate}
                        required></input>
                </div>

                <div className="check-out-date">
                    <label>Check Out Date:</label>
                    <input type="date"
                        value={checkOutDate}
                        onChange={newCheckOutDate}
                        required></input>

                </div>

                <div className="number-people">
                    <label>Number of People:</label>
                    <input type="number"
                        min={1}
                        max={20}
                        value={numOfPeople}
                        onChange={newNumOfPeople}
                        required></input>

                </div>

                <div className="number-rooms">
                    <label>Number of Rooms:</label>
                    <input type="number"
                        min={1}
                        max={20}
                        value={numOfRooms}
                        onChange={newNumOfRooms}
                        required></input>

                </div>


                <div className="button">
                    <button className="submit-button"
                        type="submit">Find Flights
                    </button>
                </div>
            </form>


        </div>

    )

}
export default Hotels
