import React, { useState } from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";
import "./Styles/Flights.css"

const Flights = () => {
    const [depAirport, setDepAirport] = useState("");
    const [arrAirport, setArrAirport] = useState("");
    const [depDate, setDepDate] = useState("");
    const [retDate, setRetDate] = useState("");
    const [flightData, setFlightData] = useState(null);
    const [numAdults, setNumAdults] = useState(0);
    const [numChildren, setNumChildren] = useState(0);
    const [numInfants, setNumInfants] = useState(0);

    const fetchData = async () => {
        try {
            const apiAccessToken = await GenerateNewToken();
            const response = await axios.get("https://test.api.amadeus.com/v2/shopping/flight-offers", {
                headers: { Authorization: `Bearer ${apiAccessToken}` },
                params: {
                    originLocationCode: depAirport,
                    destinationLocationCode: arrAirport,
                    departureDate: depDate,
                    returnDate: retDate,
                    adults: numAdults,
                    children: numChildren,
                    infants: numInfants,
                    nonStop: true,
                    currencyCode: "GBP",
                    max: 20,
                },
            });
            setFlightData(response.data);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    const submitForm = (e) => {
        e.preventDefault();
        fetchData();
    };

    return (
        <div className="flights-container">
            <h1 className="title">Search for Flights</h1>
            <form onSubmit={submitForm} className="form">
                <div className="input-group">
                    <label>From:</label>
                    <input type="text" value={depAirport} onChange={(e) => setDepAirport(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label>To:</label>
                    <input type="text" value={arrAirport} onChange={(e) => setArrAirport(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label>Departure Date:</label>
                    <input type="date" value={depDate} onChange={(e) => setDepDate(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label>Return Date:</label>
                    <input type="date" value={retDate} onChange={(e) => setRetDate(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label>Adults (13+):</label>
                    <input type="number" value={numAdults} onChange={(e) => setNumAdults(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label>Children (2-12):</label>
                    <input type="number" value={numChildren} onChange={(e) => setNumChildren(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label>Infants (0-2):</label>
                    <input type="number" value={numInfants} onChange={(e) => setNumInfants(e.target.value)} required />
                </div>
                <button type="submit" className="submit-button">Find Flights</button>
            </form>

            {flightData?.data?.length > 0 && (
                <div className="results">
                    {flightData.data.map((flightOffer, index) => (
                        <div key={index} className="flight-offer">
                            <h2>Flight Offer {index + 1}</h2>
                            <p><strong>Total Price:</strong> £{flightOffer.price.total}</p>
                            {flightOffer.itineraries.map((itinerary, iIndex) => (
                                <div key={iIndex} className="itinerary">
                                    <h3>Itinerary {iIndex + 1} (Duration: {itinerary.duration.replace("PT", "")})</h3>
                                    {itinerary.segments.map((segment, sIndex) => (
                                        <div key={sIndex} className="segment">
                                            <p><strong>Airline:</strong> {segment.carrierCode}</p>
                                            <p><strong>Flight Number:</strong> {segment.number}</p>
                                            <p><strong>Departure:</strong> {segment.departure.iataCode} (Terminal {segment.departure.terminal || "N/A"}) at {segment.departure.at.split("T")[1].slice(0, 5)}</p>
                                            <p><strong>Arrival:</strong> {segment.arrival.iataCode} (Terminal {segment.arrival.terminal || "N/A"}) at {segment.arrival.at.split("T")[1].slice(0, 5)}</p>
                                            <p><strong>Duration:</strong> {segment.duration.replace("PT", "")}</p>
                                        </div>
                                    ))}
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Flights;
