import React, {useState} from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";

// Below are all the variables required from the form
const Flights = () => {
    const [depAirport, setDepAirport] = useState("")
    const [arrAirport, setArrAirport] = useState("")
    const [depDate, setDepDate] = useState("")
    const [arrDate, setArrDate] = useState("")
    const [flightData, setFlightData] = useState(null)
    const apiAccessToken = GenerateNewToken()

    // Takes from user inputs and sets it as the current user input
    const newDepAirport = (e) => {
        setDepAirport(e.target.value)
    }

    const newArrAirport = (e) => {
        setArrAirport(e.target.value)
    }
    const newDepDate = (e) => {
        setDepDate(e.target.value)
    }

    const newArrDate = (e) => {
        setArrDate(e.target.value)
    }

    // Here is the fetchData function which is called after submit button is pressed

    const fetchData = async() => {
        // calls the API from the Amadeus website using the user input and generates a new access token if previous one expires
        try {
            const response = await axios.get('https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=${depAirport}&destinationLocationCode=${arrAirport}&departureDate=${depDate}&returnDate=${arrDate}&adults=1&nonStop=false&max=250', {
                headers: {
                    'Authorization': `Bearer ${apiAccessToken}`
                }
            });

            // Sets the flight data array with the response from the API
            setFlightData(response.data)
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      }


        const submitForm = (e) => {
            e.preventDefault()
            fetchData()
        }
    return (
        <div className="container">
            <h1>Search for Flights</h1>
            <form onSubmit={submitForm}>
                <div className="departure-airport">
                    <label>From:</label>
                    <input type="text" 
                    placeholder="Enter 3 Letters" 
                    value={depAirport}
                    onChange={newDepAirport} 
                    required></input>

                </div>
                <div className="arrival-airport">
                    <label>To:</label>
                    <input type="text"
                    placeholder="Enter 3 Letters"
                    value={arrAirport}
                    onChange={newArrAirport} 
                    required></input>
                </div>

                <div className="departure-date">
                    <label>Departure Date:</label>
                    <input type="date"
                    value={depDate}
                    onChange={newDepDate}
                    required></input>
                </div>

                <div className="arrival-date">
                    <label>Return Date:</label>
                    <input type="date"
                    value={arrDate}
                    onChange={newArrDate}
                    required></input>

                </div>

                <div className="button">
                <button className="submit-button" 
                type="submit">Find Flights
                </button>
            </div>
            </form>

            {flightData ? (
            <>
            <h1>Your Flight Results...</h1>
            <h3>From {newDepAirport} to {newArrAirport}</h3>
            </>
            ) : (
            <p>Loading flight data...</p>
            )}

        </div>

    )

}
export default Flights
