import React, { useState } from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";
const Flights = () => {
    const [depAirport, setDepAirport] = useState("")
    const [arrAirport, setArrAirport] = useState("")
    const [depDate, setDepDate] = useState("")
    const [retDate, setRetDate] = useState("")
    const [flightData, setFlightData] = useState(null)
    const [numAdults, setNumAdults] = useState(0)
    const [numChildren, setNumChildren] = useState(0)
    const [numInfants, setNumInfants] = useState(0)
    const apiAccessToken = GenerateNewToken()

    const newDepAirport = (e) => {
        setDepAirport(e.target.value)
    }

    const newArrAirport = (e) => {
        setArrAirport(e.target.value)
    }

    const newDepDate = (e) => {
        setDepDate(e.target.value)
    }

    const newRetDate = (e) => {
        setRetDate(e.target.value)
    }

    const newNumAdults = (e) => {
        setNumAdults(e.target.value)
    }

    const newNumChildren = (e) => {
        setNumChildren(e.target.value)
    }

    const newNumInfants = (e) => {
        setNumInfants(e.target.value)
    }


    const fetchData = async () => {
        try {
            const apiAccessToken = await GenerateNewToken();
            const response = await axios.get('https://test.api.amadeus.com/v2/shopping/flight-offers', {
                headers: {
                    'Authorization': `Bearer ${apiAccessToken}`
                },
                params: {
                    originLocationCode: depAirport,
                    destinationLocationCode: arrAirport,
                    departureDate: depDate,
                    returnDate: retDate,
                    adults: numAdults,
                    children: numChildren,
                    infants: numInfants,
                    nonStop: true,
                    currencyCode: 'GBP',
                    max: 20
                }
            });
            console.log("The flights API Token is: ", apiAccessToken);
            console.log(response.data);
            setFlightData(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };


    const submitForm = (e) => {
        e.preventDefault()
        fetchData()
    }

    return (
        <div className="container">
            <h1>Search for Flights</h1>
            <form onSubmit={submitForm}>
                <div className="departure-airport">
                    <label>From:</label>
                    <input type="text"
                        placeholder="Enter 3 Letters"
                        value={depAirport}
                        onChange={newDepAirport}
                        required></input>

                </div>
                <div className="arrival-airport">
                    <label>To:</label>
                    <input type="text"
                        placeholder="Enter 3 Letters"
                        value={arrAirport}
                        onChange={newArrAirport}
                        required></input>
                </div>

                <div className="departure-date">
                    <label>Departure Date:</label>
                    <input type="date"
                        value={depDate}
                        onChange={newDepDate}
                        required></input>
                </div>

                <div className="return-date">
                    <label>Return Date:</label>
                    <input type="date"
                        value={retDate}
                        onChange={newRetDate}
                        required></input>

                </div>

                <div className="people">
                    <label>Adults (13+):</label>
                    <input type="number"
                        value={numAdults}
                        onChange={newNumAdults}
                        required></input>

                    <label>Children (2-12):</label>
                    <input type="number"
                        value={numChildren}
                        onChange={newNumChildren}
                        required></input>

                    <label>Infants (0-2):</label>
                    <input type="number"
                        value={numInfants}
                        onChange={newNumInfants}
                        required></input>

                </div>

                <div className="button">
                    <button className="submit-button"
                        type="submit">Find Flights
                    </button>
                </div>
            </form>

            {flightData && flightData.data && flightData.data.length > 0 && (
                <div>
                    <h1 className="Airline">Airline: {flightData.data[0].itineraries[0]?.segments[0]?.carrierCode}</h1>
