import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './Styles/LoadMap.css'
import axios from 'axios';

function LoadMap() {
  const containerStyle = {
    width: '100%',
    height: '650px'
  };

  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  const [location, setLocation] = useState('');
  const [mapMarkers, setMapMarkers] = useState([]);
  const [selectedMapMarkers, setSelectedMapMarkers] = useState(null);
  const [PopupBox, setPopupBox] = useState(false);
  const [centre, setCentre] = useState(defaultCentre);
  const mapRef = useRef(null);

  const newLocation = (e) => {
    setLocation(e.target.value);
  };

  const fetchOverpassData = async (lat, lng) => {
    const radius = 500;
    const url = `https://overpass-api.de/api/interpreter?data=[out:json];node(around:${radius},${lat},${lng})["wheelchair"];out;`;

    try {
      const response = await axios.get(url);
      const elements = response.data.elements || [];
      return elements.map(el => ({
        lat: el.lat,
        lng: el.lon,
        name: el.tags?.name || "Unnamed OSM Place",
        address: "From OpenStreetMap",
        wheelchair: el.tags?.wheelchair || "Unknown (OpenStreetMap)",
        source: "OSM"
      }));
    } catch (error) {
      console.error("Overpass API error:", error);
      return [];
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: location }, async (results, status) => {
      if (status === 'OK' && results.length > 0) {
        const coords = results[0].geometry.location;
        const lat = coords.lat();
        const lng = coords.lng();
        setCentre({ lat, lng });

        const service = new window.google.maps.places.PlacesService(mapRef.current);
        const types = ['tourist_attraction', 'restaurant', 'museum', 'amusement_park', 'shopping_mall', 'park'];

        const searchByType = (type) => {
          return new Promise((resolve) => {
            const request = {
              location: coords,
              radius: 10000,
              type: type,
            };

            service.nearbySearch(request, (results, status) => {
              if (status === window.google.maps.places.PlacesServiceStatus.OK) {
                const detailPromises = results.map(place => {
                  return new Promise((res) => {
                    service.getDetails(
                      {
                        placeId: place.place_id,
                        fields: ['name', 'geometry', 'formatted_address', 'photo'],
                      },
                      (details, detailStatus) => {
                        if (detailStatus === window.google.maps.places.PlacesServiceStatus.OK) {
                          res({
                            lat: details.geometry.location.lat(),
                            lng: details.geometry.location.lng(),
                            name: details.name,
                            address: details.formatted_address,
                            photo: details.photos?.[0]?.getUrl({ maxWidth: 400 }) || null,
                            wheelchair: 'Wheelchair Accessibility Information is not avaliable',
                            source: "Google"
                          });
                        } else {
                          res({
                            lat: place.geometry.location.lat(),
                            lng: place.geometry.location.lng(),
                            name: place.name,
                            address: 'No Address given',
                            photo: 'No photo given',
                            wheelchair: 'Wheelchair Accessibility Information is not avaliable',
                            source: "Google"
                          });
                        }
                      }
                    );
                  });
                });

                Promise.all(detailPromises).then(resolve);
              } else {
                resolve([]);
              }
            });
          });
        };

        const googleMarkersArrays = await Promise.all(types.map(searchByType));
        const googleMarkers = googleMarkersArrays.flat();

        const osmMarkers = await fetchOverpassData(lat, lng);

        // Merge and show all
        const allCombined = [...googleMarkers, ...osmMarkers];
        setMapMarkers(allCombined);
      } else {
        alert('Location not found.');
      }
    });
  };

  const addToItinerary = async (placeName) => {
    const userID = localStorage.getItem("userID")
        console.log("User ID", userID)
        if(!userID) {
            alert("Please log in to add attraction")
        }
    try {
      const response = await axios.post("http://127.0.0.1:8000/addToItinerary/", { name: placeName , userID: userID});
      alert(response.data.message || "Added to itinerary");
    } catch (error) {
      console.error(error);
      alert("We were unable to add attraction");
    }
  };

  return (
    <div className="container">
      <h1>Find Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={newLocation}
            required
          />
          <button className="submit-button" type="submit">Search</button>
        </div>
      </form>

      <LoadScript
        googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
        libraries={['places']}
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={centre}
          zoom={13}
          onLoad={map => (mapRef.current = map)}
        >
          {mapMarkers.map((marker, index) => (
            <Marker
              key={index}
              position={{ lat: marker.lat, lng: marker.lng }}
              onClick={() => {
                setSelectedMapMarkers(marker);
                setPopupBox(true);
              }}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {PopupBox && selectedMapMarkers && selectedMapMarkers.photo && (
        <div className="modal-backdrop" onClick={() => setPopupBox(false)}>
          <div className="modal-box" onClick={e => e.stopPropagation()}>
            <h1>{selectedMapMarkers.name}</h1>
            <h2>{selectedMapMarkers.address}</h2>
            {selectedMapMarkers.source === "OSM" && selectedMapMarkers.wheelchair !== "Unknown (OpenStreetMap)" && (
  <h3>Wheelchair Accessible: {selectedMapMarkers.wheelchair}</h3>
)}

            <img className="marker-photo" src={selectedMapMarkers.photo} />
            <button onClick={() => addToItinerary(selectedMapMarkers.name)}>Add to Itinerary</button>
            <button onClick={() => setPopupBox(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoadMap;
