// Imports the React, axios, useEffect, useState and CSS file.
import axios from "axios";
import React, { useState, useEffect } from "react";

const UserRecommendations = () => {
  const [recommendations, setRecommendations] = useState([]);

  // The useEffect function fetches the data from the sendRecommendations view and the recommendations python file
  // in the django backend.
  useEffect(() => {
    const fetchRecommendations = async () => {
      const userID = localStorage.getItem("userID")
      try {
        const response = await axios.post("http://127.0.0.1:8000/sendRecommendations/", {
          user_id: userID
        })
        setRecommendations(response.data.recommendations);
        console.log("Recommendations data:", response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchRecommendations();
  }, []);

  // This part displays the recommendations to the user.
  return (
    <div className="container">
      <h1>Your Recommendations</h1>
      {Array.isArray(recommendations) && recommendations.length > 0 ? (
        recommendations.map((recommendation, id) => (
          <div key={id} className="card">
            <h2>Destination: {recommendation.name}</h2>
            <p>Score: {recommendation.score}/10</p>
          </div>
        ))
      ) : (
        <p>No recommendations found.</p>
      )}
    </div>
  );
};

export default UserRecommendations;
