import axios from "axios";
import React, { useEffect, useState } from "react";
import './Styles/Itinerary.css'
const Itinerary = () => {
    const [savedFlights, setSavedFlights] = useState([])
    const [savedHotels, setSavedHotels] = useState([])
    const [savedAttractions, setSavedAttractions] = useState([])
    const userID = localStorage.getItem("userID")

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/getFlightsFromDB/", {
          params: { user_id: userID }
        })
        .then(response => {
          console.log("Flights from backend:", response.data);
          setSavedFlights(response.data);
        })
        .catch(error => console.error("Error fetching flights:", error));
      }, []);



    return (
        <div className="container">
            <h1>Here's your itinerary...</h1>
            <ul>
                {savedFlights.map(flight => (
                    <li key={flight.id}>
                        <div className="flight-details">
                        <strong>Flight: {flight.airline} {flight.flightNum}</strong>
                        </div>

                        <div className="departure-details">
                        <strong>Departure Flight: {flight.departureAirport} {flight.departureDate} {flight.departureTime} </strong>
                        </div>

                        <div className="arrival-details">
                            <strong>Arrival Flight: {flight.arrivalAirport} {flight.arrivalDate} {flight.arrivalTime} </strong>
                        </div>

                        <div className="remove-button">
                            <button>Remove From Itinerary</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    )
}
export default Itinerary
