import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './Styles/LoadMap.css';
import axios from 'axios';

function LoadMap() {
  const containerStyle = {
    width: '100%',
    height: '650px'
  };

  const defaultCentre = {
    lat: 51.5074456,
    lng: -0.1277653
  };

  const [location, setLocation] = useState('');
  const [mapMarkers, setMapMarkers] = useState([]);
  const [selectedMapMarkers, setSelectedMapMarkers] = useState(null);
  const [PopupBox, setPopupBox] = useState(false);
  const [centre, setCentre] = useState(defaultCentre);
  const mapRef = useRef(null);

  const newLocation = (e) => {
    setLocation(e.target.value);
  };

  const fetchOSMWheelchairAccessiblePlaces = async (lat, lon) => {
    const overpassQuery = `
      [out:json];
      (
        node[wheelchair="yes"](around:200,${lat},${lon});
        way[wheelchair="yes"](around:200,${lat},${lon});
        relation[wheelchair="yes"](around:200,${lat},${lon});
      );
      out center;
    `;

    try {
      const response = await fetch("https://overpass-api.de/api/interpreter", {
        method: "POST",
        body: overpassQuery,
      });

      const data = await response.json();

      const markers = data.elements.map((element) => {
        const coords = element.type === "node"
          ? { lat: element.lat, lng: element.lon }
          : { lat: element.center.lat, lng: element.center.lon };

        // Get the wheelchair tag
        const wheelchairAccess = element.tags?.wheelchair || "unknown";

        // Set the marker color based on the wheelchair tag
        let markerColor = '#808080'; // Default to gray (unknown)
        if (wheelchairAccess === "yes") {
          markerColor = 'green';
        } else if (wheelchairAccess === "limited") {
          markerColor = 'orange';
        } else if (wheelchairAccess === "no") {
          markerColor = 'red';
        }

        return {
          lat: coords.lat,
          lng: coords.lng,
          name: element.tags?.name || "Accessible Place",
          wheelchair: wheelchairAccess,
          markerColor: markerColor, // Store the color with the marker
        };
      });

      return markers;
    } catch (error) {
      console.error("Error fetching Overpass API data:", error);
      return [];
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();

    const geocoder = new window.google.maps.Geocoder();

    geocoder.geocode({ address: location }, async (results, status) => {
      if (status === 'OK' && results.length > 0) {
        const coords = results[0].geometry.location;
        const lat = coords.lat();
        const lng = coords.lng();

        setCentre({ lat, lng });

        // Fetch only wheelchair accessible places from OSM
        const accessibleMarkers = await fetchOSMWheelchairAccessiblePlaces(lat, lng);
        setMapMarkers(accessibleMarkers);

      } else {
        alert('Location not found.');
      }
    });
  };

  const addToItinerary = async (placeName) => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/addToItinerary/", { name: placeName });
      alert(response.data.message || "Added to itinerary");
    } catch (error) {
      console.error(error);
      alert("We were unable to add attraction");
    }
  };

  return (
    <div className="container">
      <h1>Find Wheelchair Accessible Activities</h1>
      <form onSubmit={submitForm}>
        <div className="location">
          <label>Location:</label>
          <input
            type="text"
            placeholder="Enter your Location"
            value={location}
            onChange={newLocation}
            required
          />
          <button className="submit-button" type="submit">Search</button>
        </div>
      </form>

      <LoadScript
        googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
        libraries={['places']}
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={centre}
          zoom={12}
          onLoad={map => (mapRef.current = map)}
        >
          {mapMarkers.map((marker, index) => (
            <Marker
              key={index}
              position={{ lat: marker.lat, lng: marker.lng }}
              icon={{
                url: `http://maps.google.com/mapfiles/ms/icons/${marker.markerColor}-dot.png`, // Use custom color
                scaledSize: new window.google.maps.Size(32, 32), // Optional: adjust the size
              }}
              onClick={() => {
                setSelectedMapMarkers(marker);
                setPopupBox(true);
              }}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {PopupBox && selectedMapMarkers && (
        <div className="modal-backdrop" onClick={() => setPopupBox(false)}>
          <div className="modal-box" onClick={e => e.stopPropagation()}>
            <h2>{selectedMapMarkers.name}</h2>

            {/* Wheelchair accessibility visual indicator */}
            <p>
              <strong>Wheelchair Accessible:</strong>
              {selectedMapMarkers.wheelchair === "yes" && (
                <span>
                  <img
                    src="https://img.icons8.com/ios-filled/50/00e676/wheelchair.png"
                    alt="Wheelchair accessible"
                    style={{ width: 24, marginRight: 8 }}
                  />
                  Fully Accessible
                </span>
              )}
              {selectedMapMarkers.wheelchair === "limited" && (
                <span>
                  <img
                    src="https://img.icons8.com/ios-filled/50/ff9800/wheelchair.png"
                    alt="Wheelchair limited"
                    style={{ width: 24, marginRight: 8 }}
                  />
                  Partially Accessible
                </span>
              )}
              {selectedMapMarkers.wheelchair === "no" && (
                <span>
                  <img
                    src="https://img.icons8.com/ios-filled/50/f44336/wheelchair.png"
                    alt="Wheelchair inaccessible"
                    style={{ width: 24, marginRight: 8 }}
                  />
                  Not Accessible
                </span>
              )}
              {selectedMapMarkers.wheelchair === "unknown" && (
                <span>
                  <img
                    src="https://img.icons8.com/ios-filled/50/9e9e9e/wheelchair.png"
                    alt="Wheelchair unknown"
                    style={{ width: 24, marginRight: 8 }}
                  />
                  Accessibility Unknown
                </span>
              )}
            </p>

            {/* Add to itinerary button */}
            <button onClick={() => addToItinerary(selectedMapMarkers.name)}>Add to Itinerary</button>

            {/* Close button */}
            <button onClick={() => setPopupBox(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoadMap;
