import React, { useState, useEffect } from "react";
import axios from "axios";
import { GenerateNewToken } from "./APIs/FlightsAPI";

const Flights = () => {
    const [depAirport, setDepAirport] = useState("");
    const [arrAirport, setArrAirport] = useState("");
    const [depDate, setDepDate] = useState("");
    const [arrDate, setArrDate] = useState("");
    const [flightData, setFlightData] = useState(null);
    const [apiAccessToken, setApiAccessToken] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchToken = async () => {
            try {
                const token = await GenerateNewToken();
                setApiAccessToken(token);
            } catch (err) {
                console.error("Failed to generate API token", err);
                setError("Failed to authenticate with the flight data provider.");
            }
        };

        fetchToken();
    }, []);

    const handleDepAirportChange = (e) => setDepAirport(e.target.value);
    const handleArrAirportChange = (e) => setArrAirport(e.target.value);
    const handleDepDateChange = (e) => setDepDate(e.target.value);
    const handleArrDateChange = (e) => setArrDate(e.target.value);

    const fetchData = async () => {
        if (!apiAccessToken) {
            setError("API token not available.");
            return;
        }

        setLoading(true);
        setError(null);

        try {
            const response = await axios.get(
                `https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=${depAirport}&destinationLocationCode=${arrAirport}&departureDate=${depDate}&returnDate=${arrDate}&adults=1&nonStop=false&max=250`,
                {
                    headers: {
                        'Authorization': `Bearer ${apiAccessToken}`
                    }
                }
            );

            setFlightData(response.data);
        } catch (err) {
            console.error("Error fetching flight data:", err);
            setError("An error occurred while fetching flight data.");
        } finally {
            setLoading(false);
        }
    };

    const submitForm = (e) => {
        e.preventDefault();
        fetchData();
    };

    return (
        <div className="container">
            <h1>Search for Flights</h1>
            <form onSubmit={submitForm}>
                <div className="departure-airport">
                    <label>From:</label>
                    <input
                        type="text"
                        placeholder="Enter 3 Letters"
                        value={depAirport}
                        onChange={handleDepAirportChange}
                        required
                    />
                </div>
                <div className="arrival-airport">
                    <label>To:</label>
                    <input
                        type="text"
                        placeholder="Enter 3 Letters"
                        value={arrAirport}
                        onChange={handleArrAirportChange}
                        required
                    />
                </div>
                <div className="departure-date">
                    <label>Departure Date:</label>
                    <input
                        type="date"
                        value={depDate}
                        onChange={handleDepDateChange}
                        required
                    />
                </div>
                <div className="arrival-date">
                    <label>Return Date:</label>
                    <input
                        type="date"
                        value={arrDate}
                        onChange={handleArrDateChange}
                        required
                    />
                </div>
                <div className="button">
                    <button className="submit-button" type="submit">
                        Find Flights
                    </button>
                </div>
            </form>

            {loading && <p>Loading flight data...</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}

            {flightData && !loading && (
                <>
                    <h1>Your Flight Results...</h1>
                    <h3>From {depAirport} to {arrAirport}</h3>
                </>
            )}
        </div>
    );
};

export default Flights;
